## 最终代码

处理完毕后最终代码如下

```c++
#include<stdio.h>
#include<stack>
#include<map>
#include<math.h>
typedef char oper;//运算符
typedef float operand;
std::stack<oper> history;//历史栈 
std::stack<operand> operands;

/*优先数表*/
struct prv_num_t{char left,right;};//定义结构体 prv_num_t  _t代表类型 
//关键字只包含一部分数据项,就称为map
//关键字包含全部数据项,成为 
std::map<oper, prv_num_t> p_num={
	{'+',{81,82}},
	{'-',{81,82}},
	{'*',{41,42}},
	{'/',{41,42}},
	{'=',{100,100}},
	{'^',{32,31}}, 
	{'L',{22,21}},//对数运算符log(x,y) ；log (x,log(x,x)) ，右边优先 
	{'S',{22,21}},//定义sin函数 
	{'C',{22,21}},//定义cos函数; 
};


/*op1在左，op2在右*/
/*返回值为负: op1优先级高于op2*/
/*返回值为正: op2优先级高于op1*/
/*返回值为0: op1优先级等于op2*/

int prior(oper op1, oper op2){
	return p_num[op1].left - p_num[op2].right;
}

#include<string.h>
operand calc(){
	operand x, y;
//	if(history.top()是双目运算符){
	if(strchr("+-*/^L",history.top())){//strchr在 "+-*/^L"找到 history.top()，如果找到返回位置，找不到返回空值【用来判断（整数和0）】 
		y = operands.top();
		operands.pop();
	}
	x = operands.top();
	operands.pop();
	switch(history.top()){
		case '+': return x+y;
		case '-': return x-y;
		case '*': return x*y;
		case '/': return x/y;
		case '^': return pow(x,y);
		case 'L': return log(y)/log(x);//log c库的对数换底公式
		case 'S': return sin(y); 
		case 'C': return cos(y); 
	}
	printf("Err");
	return -1;
}


operand solve(){
	operand x, y;
	oper op;

	history.push('=');//输入等号，whach dog（士兵）进栈 
	while(1){
/****************************************
*2.  输入下一个运算符op;
*3.  while（h.top()比op优先）{	栈顶元素出栈计算；}
*4.  if(h.top()=='='&& op=='='){结束}
*5.  h.push(op);
*6.  转2。
******************************************/
	scanf(" %c",&op);//预读下一位数 
//	if(op是数字)
	if(isdigit(op)) {//isdigiy判断是否是数字 
		ungetc(op, stdin);//还回字符 ,还给stdin（键盘） 
		scanf("%f",&x);
		operands.push(x);
		continue; 
	}	
		while(prior(history.top(), op)<0){
			operands.push(calc());
			history.pop();
		}
		if(prior(history.top(),op)==0) break;
		history.push(op);
	}
	return operands.top();
}

int main(){
	printf("%g\n",solve());

	return 0;
}

