	operand x, y;
	oper op;
	std::stack<oper> history;//历史栈 
	std::stack<operand> operands;
	history.push('=');//输入等号，whach dog（士兵）进栈 
	while(1){
/*******************
*2.  输入下一个运算符op;
*3.  while（h.top()比op优先）{	栈顶元素出栈计算；}
*4.  if(h.top()=='='&& op=='='){结束}
*5.  h.push(op);
*6.  转2。
********************/
		scanf("%f %c",&x,&op);
		operands.push(x);
		while(prior(history.top(), op)<0){
			y = operands.top();
			operands.pop();
			x = operands.top();
			operands.pop();
			switch(history.top()){
				case '+': operands.push(x + y) ; break;
				case '-': operands.push(x - y) ; break;
				case '*': operands.push(x * y) ; break;
				case '/': operands.push(x / y) ; break;
			}
			history.pop();
		}
		if(prior(history.top(),op)==0) break;
		history.push(op);
	}
    return operands.top();
}
```



